import React, { useState } from 'react';
import questionsData from '../data/questions.json';
import Question from './Question';

const Quiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [fontSize, setFontSize] = useState(16);

  const handleAnswer = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      setAnswers(prevAnswers => ({
        ...prevAnswers,
        [name]: {
          ...(prevAnswers[name] || {}),
          [value]: checked,
        },
      }));
    } else {
      setAnswers(prevAnswers => ({
        ...prevAnswers,
        [name]: value,
      }));
    }
  };

  const handleNext = () => {
    const currentQuestion = questionsData[currentQuestionIndex];

    if (currentQuestion.type === 'one-choice') {
      const selectedAnswerIndex = answers[`question-${currentQuestion.id}`];
      if (selectedAnswerIndex !== undefined) {
        const isCorrect = parseInt(selectedAnswerIndex, 10) === currentQuestion.correctOption;
        alert(isCorrect 
          ? 'Resposta correta!' 
          : `Resposta incorreta. A resposta correta é: ${currentQuestion.options[currentQuestion.correctOption]}`);
      } else {
        alert('Nenhuma opção selecionada.');
      }
    } 
    
    if (currentQuestion.type === 'multiple-choice') {
      const selectedAnswers = answers[`question-${currentQuestion.id}`] || {};
      const correctAnswers = new Set(currentQuestion.correctOptions);
      const userAnswers = new Set(Object.keys(selectedAnswers).filter(key => selectedAnswers[key] === true).map(Number));
      
      console.log("Selected Answers:", selectedAnswers);
      console.log("Correct Answers:", [...correctAnswers]);
      console.log("User Answers:", [...userAnswers]);

      const isCorrect = correctAnswers.size === userAnswers.size && [...correctAnswers].every(answer => userAnswers.has(answer));
      alert(isCorrect 
        ? 'Resposta correta!' 
        : `Resposta incorreta. As respostas corretas são: ${currentQuestion.correctOptions.map(index => currentQuestion.options[index]).join(', ')}`);
    }
    
    if (currentQuestion.type === 'input') {
      const userAnswer = answers[`question-${currentQuestion.id}`] || '';
      const isCorrect = userAnswer.trim() === currentQuestion.correctAnswer.trim();
      alert(isCorrect 
        ? 'Resposta correta!' 
        : `Resposta incorreta. A resposta correta é: ${currentQuestion.correctAnswer}`);
    }
    
    if (currentQuestionIndex < questionsData.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setCurrentQuestionIndex(0); // Reinicia o índice para permitir o recomeço
    }
  };

  const handleRestart = () => {
    setCurrentQuestionIndex(0);
    setAnswers({});
    setFontSize(16); // Resetando o zoom, se necessário
  };

  const calculateProgress = () => {
    return ((currentQuestionIndex + 1) / questionsData.length) * 100;
  };

  const currentQuestion = questionsData[currentQuestionIndex];

  if (!currentQuestion) {
    return (
      <div>
        <div className="progress-bar" style={{ width: `${calculateProgress()}%` }}></div>
        <div className="zoom-controls">
          <button onClick={() => setFontSize(fontSize + 2)}>Aumentar Zoom</button>
          <button onClick={() => setFontSize(fontSize - 2)}>Diminuir Zoom</button>
        </div>
        <div className="question-container" style={{ fontSize: `${fontSize}px` }}>
          <div>Quiz completo!</div>
          <button onClick={handleRestart}>Recomeçar</button>
        </div>
      </div>
    );
  }

  const increaseFontSize = () => setFontSize(fontSize + 2);
  const decreaseFontSize = () => setFontSize(fontSize - 2);

  return (
    <div>
      <div className="progress-bar" style={{ width: `${calculateProgress()}%` }}></div>
      <div className="zoom-controls">
        <button onClick={increaseFontSize}>Aumentar Zoom</button>
        <button onClick={decreaseFontSize}>Diminuir Zoom</button>
      </div>
      <div className="question-container" style={{ fontSize: `${fontSize}px` }}>
        <Question question={currentQuestion} onAnswer={handleAnswer} />
        <button onClick={handleNext}>
          {currentQuestionIndex < questionsData.length - 1 ? 'Próxima' : 'Finalizar'}
        </button>
      </div>
    </div>
  );
};

export default Quiz;
