import React, { useState } from 'react';
import questionsData from '../data/questions.json';

const Quiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});

  const handleAnswer = (e) => {
    const { name, value } = e.target;
    setAnswers({ ...answers, [name]: value });
  };

  const handleNext = () => {
    if (currentQuestionIndex < questionsData.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const calculateProgress = () => {
    return ((currentQuestionIndex + 1) / questionsData.length) * 100;
  };

  const currentQuestion = questionsData[currentQuestionIndex];

  if (!currentQuestion) {
    return <div>Quiz completo!</div>;
  }

  return (
    <div>
      <div className="progress-bar" style={{ width: `${calculateProgress()}%` }}></div>
      <div className="question-container">
        <h2>{currentQuestion.title}</h2>
        {currentQuestion.options && currentQuestion.options.length > 0 ? (
          currentQuestion.options.map((option, index) => (
            <div key={index}>
              <label>
                <input
                  type={currentQuestion.type === 'one-choice' ? 'radio' : 'checkbox'}
                  name={`question-${currentQuestion.id}`}
                  value={option}
                  onChange={handleAnswer}
                />
                {option}
              </label>
            </div>
          ))
        ) : (
          <p>Sem opções disponíveis.</p>
        )}
        <button onClick={handleNext} disabled={currentQuestionIndex >= questionsData.length - 1}>
          Próxima
        </button>
      </div>
    </div>
  );
};

export default Quiz;
