import React, { useState } from 'react';
import questionsData from '../data/questions.json';

const Quiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});

  const handleAnswer = (e) => {
    const { name, value, type, checked } = e.target;

    console.log(`Handling answer for ${name}. Type: ${type}, Value: ${value}, Checked: ${checked}`);

    if (type === 'checkbox') {
      setAnswers(prevAnswers => ({
        ...prevAnswers,
        [name]: {
          ...(prevAnswers[name] || {}),
          [value]: checked,
        },
      }));
    } else {
      setAnswers({ ...answers, [name]: value });
    }
  };

  const handleNext = () => {
    const currentQuestion = questionsData[currentQuestionIndex];

    console.log('Current Question:', currentQuestion);

    if (currentQuestion.type === 'one-choice') {
      const selectedAnswer = answers[`question-${currentQuestion.id}`];
      console.log('Selected Answer for one-choice:', selectedAnswer);
      if (selectedAnswer) {
        const isCorrect = selectedAnswer === currentQuestion.options[currentQuestion.correctOption];
        alert(isCorrect 
          ? 'Resposta correta!' 
          : `Resposta incorreta. A resposta correta é: ${currentQuestion.options[currentQuestion.correctOption]}`);
      } else {
        alert('Nenhuma opção selecionada.');
      }
    } 
    
    if (currentQuestion.type === 'multiple-choice') {
      const selectedAnswers = answers[`question-${currentQuestion.id}`] || {};
      const correctAnswers = new Set(currentQuestion.correctOptions);
      const userAnswers = new Set(Object.keys(selectedAnswers).filter(key => selectedAnswers[key] === true).map(Number));
      
      console.log('Selected Answers for multiple-choice:', selectedAnswers);
      console.log('User Answers for multiple-choice:', userAnswers);
      
      const isCorrect = correctAnswers.size === userAnswers.size && [...correctAnswers].every(answer => userAnswers.has(answer));
      alert(isCorrect 
        ? 'Resposta correta!' 
        : `Resposta incorreta. As respostas corretas são: ${currentQuestion.correctOptions.map(index => currentQuestion.options[index]).join(', ')}`);
    }
    
    if (currentQuestion.type === 'input') {
      const userAnswer = answers[`question-${currentQuestion.id}`] || '';
      console.log('User Answer for input:', userAnswer);
      console.log('Correct Answer for input:', currentQuestion.correctAnswer);
      const isCorrect = userAnswer.trim() === currentQuestion.correctAnswer.trim(); // Comparação segura de strings
      alert(isCorrect 
        ? 'Resposta correta!' 
        : `Resposta incorreta. A resposta correta é: ${currentQuestion.correctAnswer}`);
    }
    
    if (currentQuestionIndex < questionsData.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const calculateProgress = () => {
    return ((currentQuestionIndex + 1) / questionsData.length) * 100;
  };

  const currentQuestion = questionsData[currentQuestionIndex];

  if (!currentQuestion) {
    return <div>Quiz completo!</div>;
  }

  console.log('Current Answers:', answers);

  return (
    <div>
      <div className="progress-bar" style={{ width: `${calculateProgress()}%` }}></div>
      <div className="question-container">
        <h2>{currentQuestion.title}</h2>
        {currentQuestion.type === 'one-choice' && currentQuestion.options && (
          currentQuestion.options.map((option, index) => (
            <div key={index}>
              <label>
                <input
                  type="radio"
                  name={`question-${currentQuestion.id}`}
                  value={option}
                  onChange={handleAnswer}
                />
                {option}
              </label>
            </div>
          ))
        )}
        {currentQuestion.type === 'multiple-choice' && currentQuestion.options && (
          currentQuestion.options.map((option, index) => (
            <div key={index}>
              <label>
                <input
                  type="checkbox"
                  name={`question-${currentQuestion.id}`}
                  value={index}
                  checked={answers[`question-${currentQuestion.id}`]?.[index] || false}
                  onChange={handleAnswer}
                />
                {option}
              </label>
            </div>
          ))
        )}
        {currentQuestion.type === 'input' && (
          <div>
            <input
              type="text"
              name={`question-${currentQuestion.id}`}
              value={answers[`question-${currentQuestion.id}`] || ''}
              onChange={handleAnswer}
            />
          </div>
        )}
        <button onClick={handleNext} disabled={currentQuestionIndex >= questionsData.length - 1}>
          Próxima
        </button>
      </div>
    </div>
  );
};

export default Quiz;
